<?phpclass Privacypolicymodel extends CI_Model {    function __construct() {       parent::__construct();    }    function index(){        $this->db->select('*');        $this->db->from('el_privacy_policy');        $this->db->where('policy_status','1');        $query = $this->db->get();        $num = $query->num_rows();        if($num>0){            //print_r($query->row_array()); exit;            //echo "<pre>";            $result = $query->result();            return $result;        }else{            return 0;        }		    }    function addPrivacypolicy($data) {		if(!empty($data)){ 			$this->db->insert('el_privacy_policy', $data);						$insert_id = $this->db->insert_id();						return 1;			//return 1;		}		else{						return 0;					}    }    // Edit     function Privacypolicyedit($id){        //        if(isset($id) && !empty($id) && $id!=0){        $this->db->select('*');        $this->db->from('el_privacy_policy');        $this->db->where('policy_id',$id);        $query = $this->db->get();        $num = $query->num_rows();        if($num>0){            //print_r($query->row_array()); exit;            //echo "<pre>";            $result = $query->row_array();            return $result;        }else{            return 0;        }                       }    }    // Update    function updatePrivacypolicybyId($fid, $data) {		 if($data!=''){			$this->db->where('policy_id', $fid);			$this->db->update('el_privacy_policy', $data);			return 1;		} else{			return 0;		}          }    // Delete Page    function deletePrivacypolicybyId($fid){        if(isset($fid) && !empty($fid)){            $data =  array('policy_status'=>'0');            $this->db->where('policy_id', $fid);            $this->db->update('el_privacy_policy', $data);            return 1;        } else{                return 0;        }    }    }